import React from 'react';
import { useState } from 'react';
import PopForm from '../home/popForm';



function FirebaseCon() {
    render()

    const { RecipientName, PhoneNumber, DateTime, ShippingCompany, ShippmentNumber } = { details };
    const [details, setDetails] = useState({
        RecipientName: '',
        PhoneNumber: '',
        DateTime: '',
        ShippingCompany: '',
        ShippmentNumber: '',

    })

    console.warn(props);

    const PostData = async (e) => {
        e.preventDefault()



        const res = await fetch("https://dispatcher-system-default-rtdb.firebaseio.com/amitform.json",
            {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    RecipientName,
                    PhoneNumber,
                    DateTime,
                    ShippingCompany,
                    ShippmentNumber,
                })
            })
    }

    return (
        <div className='form' >
            <input type='text' placeholder='Enter your first name' onChange={(e) =>
                setDetails({ ...details, fName: e.target.value })} />
            <input type='text' placeholder='Enter your last name' onChange={(e) =>
                setDetails({ ...details, lName: e.target.value })} />
            <input type='email' placeholder='Enter your Email address' onChange={(e) =>
                setDetails({ ...details, email: e.target.value })} />
            <button onClick={PostData}>Submit</button>
        </div>
    )
}

export default FirebaseCon;
